import { useState, useEffect } from 'react';
import Gamepad from './gamepad.js'
import SelectorSwitch from './SelectorSwitch.js'



const App = () => {

  const fakeGamePad = {
    id: "fake-gamepad",
    index: 0,
    mapping: "",
    hand: "",
    connected: true,
    buttons: [],
    axes: [],
    timestamp: 0,
    pose: GamepadPose,
